function highlightJavaScriptSyntax() {
    lively.morphic.Text.prototype.highlightJavaScriptSyntax.apply(this);
    var currentMs = Date.now();
    if (this.lastDFASyntaxHighlightTime && (currentMs-this.lastDFASyntaxHighlightTime < 300))
        return;
    this.lastDFASyntaxHighlightTime = currentMs;
    require('lively.ast.StaticAnalysis').toRun((function() {
        this.advancedHighlighting();
    }).bind(this));
}

