function changeBackground() {
    var date, sliderValue;
    sliderValue = this.owner.get('Slider').getValue();
    sliderValue = (10e3 - 10) * (Math.pow(sliderValue, 2) / 4) + 10;
    date = (new Date().getTime() + this.seed) / sliderValue;
    this.owner.owner.owner.setFill(
        Color.rgb(
            (Math.sin(date/3) / 2 + 0.5) * 256,
            (Math.sin(date/7) / 2 + 0.5) * 256,
            (Math.sin(date/13) / 2 + 0.5) * 256
        )
    );
}
