function doAction() {
    var heightAfterCollapsing = 40;
    if (this.collapsed) {
        this.hiddenMorphs.each(function(m){this.addMorph(m)}, this.owner);
        delete this.hiddenMorphs;
        this.owner.setExtent(this.previousExtent);
        delete this.previousExtent;
        this.setLabel('-');
    } else {
        this.hiddenMorphs = this.owner.submorphs.select(function(m) {
            return (m.name.indexOf('ModuleLabel') < 0 && m != this&&
m.name.indexOf('Icon') < 0);
        }, this);
        this.hiddenMorphs.each(function(m){m.remove()});
        this.previousExtent = this.owner.getExtent();
        this.owner.setExtent(this.previousExtent.withY(heightAfterCollapsing));
        this.setLabel('v');
    }
    this.collapsed = !this.collapsed;
}

