function drawSeries(contexts, data, scales) {
    var series = data.getSeries(),
        width = this.barWidth,
        xValueName = this.xValueName || 'x',
        yValueName = this.yValueName || 'y';

    if (scales.x && scales.y) {
        contexts.each(function(ctx, i){
            var s, ser,
                ctxSer = [],
                x = scales.x[i] || scales.x.first(),
                y = scales.y[i] || scales.y.first();

            if (x && y) {
                for (var b = i; (s = series[b]); b += contexts.length) {
                        ctxSer.push(s);
                    }
                var seriesCount = ctxSer.length,
                    seriesRepresentation = ctx.selectAll("g.series")
                        .data(ctxSer);
                seriesRepresentation.enter()
                    .insert("svg:g")
                    .attr("class", "series");
                seriesRepresentation.exit()
                    .remove();

                seriesRepresentation.each(function(d, i) {
                    var xMargin = - width/2 + i * (width / seriesCount),
                        bars = d3.select(this).selectAll('.bar')
                            .data(d.values);
                    bars.enter()
                        .insert("svg:rect")
                        .attr("class", 'bar'+ (d.className ? ' '+ d.className : ''))
                        .attr("height", function(d) { return  0; } )
                        .attr("y", function(d) { return y(0); });
                    bars.exit()
                            .remove();
                    bars.attr("width", (width / seriesCount))
                         .attr("x", function(d) { return x(d[xValueName]) + xMargin; })
                    bars.transition().duration(1000)
                        .attr("height", function(d) { return  y(0)- y(d[yValueName]) ; } )
                        .attr("y", function(d) { return y(d[yValueName]); });
                    });
            } else {
                console.warn('Cannot draw bars when no scales are defined');
            }
        });
    }
}
