function setupScales(data, areas) {
    var invNeg = this.invertNegative, // how are negative values displayed?
        xScales = areas.collect(function(area){
                var w = area.width,
                    h = area.height;
                return d3.scale.linear().range([0, w]).domain([this.xMin, this.xMax]);
            }, this),
        yScales = areas.collect(function(area){
                var w = area.width,
                    h = area.height;
                return d3.scale.linear().range([h+1, 0]).domain([0, this.yMax]);
            }, this),
        negaYScales = areas.collect(function(area){
                var w = area.width,
                    h = area.height;
                return d3.scale.linear()
                    .range(invNeg ? [h+1, 0] : [-1, h])
                    .domain([0, -this.yMax]);
            }, this);
    return {x: xScales, y: yScales, negaY: negaYScales};
}

