function onChartDrag(evt) {
    var xDelta = evt.clientX- this.startXDrag || 0,
        yDelta = evt.clientY - this.startYDrag || 0,
        xMinStart = this.xMinStart || 0,
        xMaxStart = this.xMaxStart || 0,
        yMaxStart = this.yMaxStart || 0,
        rangeFactor = (xMaxStart - xMinStart) / 300, //FIXME: has to adapt to larger chartareas
        yRangeFactor = yMaxStart / 300, //FIXME: has to adapt to larger chartareas
        newXMin = xMinStart - xDelta * rangeFactor,
        newXMax = xMaxStart - xDelta * rangeFactor,
        newYMax = yMaxStart + yDelta * yRangeFactor;
    if (this.xMinSlider && this.xMaxSlider && this.yMaxSlider) {
        this.xMinSlider.value = newXMin;
        this.xMaxSlider.value = newXMax;
        this.yMaxSlider.value = newYMax;
    }
}

