function onChartMouseWheel(evt) {
    var xMinSlider = this.xMinSlider,
        xMaxSlider = this.xMaxSlider;

    if (xMinSlider && xMaxSlider) {
        var xMinStart = xMinSlider.value,
        xMaxStart = xMaxSlider.value,
        range = xMaxStart - xMinStart,
        wheelFactor = 0.001,
        wheelDelta = evt.wheelDeltaY,
        zoomAddRange = range * wheelDelta * wheelFactor;

        // Avoid page scrolling
        evt.preventDefault();

        xMinSlider.value = xMinStart + zoomAddRange;
        xMaxSlider.value = xMaxStart - zoomAddRange;
    } else {
        console.warn('Where are my sliders? Cannot zoom!');
    }

}

