function drawSeries(contexts, data, scales) {
    var series = data.getSeries(),
        tension = this.tension || 1,
        interpolation = this.interpolation || 'linear',
        bandwidth = this.yMax,
        bands = this.bands || 1;

    // Fix Array.toString(): [1,2,3,4] -> "1,2,3,4"
    Array.prototype.toString =
        function() {
            return this.reduce(
                function(previousValue, currentValue, index, array) {
                    return (previousValue.length > 0
                            ? previousValue +','
                            : '')
                            + currentValue;
                }, '');
        };
    if (contexts.length !== series.length) {
        alert('Error in Horizon Graph module: not same amount of contexts and series ('
            +contexts.length+' vs. '+series.length+')!');
        return
    }

    if (scales.x && scales.y && scales.negaY) {
        contexts.each(function(ctx, i){
            var positiveBands,
                negativeBands
                s = series[i],
                x = scales.x[i] || scales.x.first(),
                y = scales.y[i] || scales.y.first(),
                negaY = scales.negaY[i] || scales.negaY.first(),
                positiveArea = d3.svg.area()
                        .interpolate(interpolation)
                        .tension(tension)
                        .x(function(d) { return x(d.x); })
                        .y0(function(d) { return y(d.y); })
                        .y1(function(d) { return y(0); }),
                negativeArea = d3.svg.area()
                        .interpolate(interpolation)
                        .tension(tension)
                        .x(function(d) { return x(d.x); })
                        .y0(function(d) { return negaY(d.y); })
                        .y1(function(d) { return negaY(0); }),
                positiveDataBands = [],
                negativeDataBands = [];

            // One value array for each band
            for (var i = 0; i < bands; i++) {
                positiveDataBands.push([]);
                negativeDataBands.push([]);
            }

            // Prepare the data for the horizon bands
            s.values.each(function(val) {
                    var value = val.y;
                    for (var i = 0; i < bands; i++) {
                        positiveDataBands[i].push({
                            x: val.x,
                            y: value - bandwidth * i
                        });
                        negativeDataBands[i].push({
                            x: val.x,
                            y: value + bandwidth * i
                        });
                    }
                });

            if (x && y && negaY) {
                positiveBands = ctx.selectAll("path.series.horizon.positive")
                    .data(positiveDataBands);
                positiveBands.enter()
                    .insert("svg:path");
                positiveBands.exit()
                    .remove();

                positiveBands.each(function(d, i) {
                    d3.select(this)
                        .datum(d)
                        .attr("d", positiveArea)
                        .attr("class", 'series horizon positive band'+i);
                });

                negativeBands = ctx.selectAll("path.series.horizon.negative")
                    .data(negativeDataBands);
                negativeBands.enter()
                    .insert("svg:path");
                negativeBands.exit()
                    .remove();

                negativeBands.each(function(d, i) {
                    d3.select(this)
                        .datum(d)
                        .attr("d", negativeArea)
                        .attr("class", 'series horizon negative band'+i);
                });
            } else {
                console.warn('Cannot draw lines when no scales are defined');
            }
        });
    }

}

