function setupDrawingAreas(data, area) {
    var nw = area.width,
        nh = area.height,
        p = 0, // no padding
        t = area.y,
        l = area.x,
        pt = this.paddingTop || 0,
        pb = this.paddingBottom || 0,
        pl = this.paddingLeft || 0,
        pr = this.paddingRight || 0;
        s = data.getSeries().length,
        r = s,
        newArea = new Rectangle(l + pl, t + pt, nw - pl - pr, nh - pt - pb),
        w = newArea.width,
        h = newArea.height,
        panelsPerRow = Math.ceil(s/ r),
        panelsPerColumn = Math.ceil(s/ panelsPerRow),
        panelWidth = (w - (panelsPerRow - 1) * p) / panelsPerRow,
        panelHeight = (h- (panelsPerColumn - 1) * p) / panelsPerColumn,
        areas = [];

    for (var i = 0; i < s; i++) {
        var row = Math.floor(i/panelsPerRow),
            col = i % panelsPerRow;
        areas.push(new Rectangle(
            newArea.x + col * (p + panelWidth),
            newArea.y + row * (p + panelHeight),
            panelWidth,
            panelHeight
            ));
    }
    return areas;

}
