function drawSeries(contexts, data, scales) {
    var series = data.getSeries(),
        circleSizeFactor = (this.circleSizeFactor >=0) ? this.circleSizeFactor: 1 ;

    if (scales.x && scales.loc) {
        contexts.each(function(ctx, i){
            var s, ser,
                ctxSer = [],
                x = scales.x[i] || scales.x.first(),
                loc = scales.loc[i] || scales.loc.first();

            if (x && loc) {
                for (var b = i; (s = series[b]); b += contexts.length) {
                        ctxSer.push(s);
                    }
                var seriesCount = ctxSer.length,
                    selectVisiblePoints = (loc.origin) ?
                        // If we deal with an azimuthal projection we want to
                        // select only the points that are on the visible half
                        // of the earth
                        function(val){
                                if (val.loc && val.loc.lng && val.loc.lat) {
                                    var p = {type:'Point',
                                            coordinates:[val.loc.lng, val.loc.lat]};
                                    return d3.geo.greatCircle()
                                        .origin(loc.origin()).clip(p);
                                } // in case there are no coordinates, do not draw
                            } :
                        function() {return true},
                    seriesRepresentation = ctx.selectAll("g.series")
                        .data(ctxSer);

                seriesRepresentation.enter()
                    .insert("svg:g")
                    .attr("class", "series");
                seriesRepresentation.exit()
                    .remove();

                seriesRepresentation.each(function(d, i) {
                    var circles = d3.select(this).selectAll('circle')
                            .data(d.values.select(selectVisiblePoints));
                    circles.enter()
                        .insert("svg:circle")
                        .attr("class", 'city'+ (d.className ? ' '+ d.className : ''));
                    circles.exit()
                        .remove();
                    try {
                        circles
                            .attr("r", function(d) { return x(d.y) * circleSizeFactor; })
                            .attr("cy", function(d) { return loc([d.loc.lng, d.loc.lat])[1]; })
                            .attr("cx", function(d) { return loc([d.loc.lng, d.loc.lat])[0]; })
                    } catch(e) {
                        console.warn(e);
                    }
                });
            } else {
                console.warn('Cannot draw bars when no scales are defined');
            }
        });
    }
}
