function setupScales(data, areas) {
    var circleSize = this.circleSize || 0,
        sizeDownFactor = 10;
    return {x: areas.collect(function(area){
                var w = Math.sqrt(area.width * area.height) / sizeDownFactor*circleSize;
                return d3.scale.linear().range([0, w]).domain([0, 10]);
            }, this)};
}
