684.source:
function drawDimensions(context, dimensions, scales, areas) {
    var drawingPanes = [],
        xTickCount = this.xTickCount || 0,
        xLabelRotation = this.xLabelRotation || 0,
        makeDrawingPane = this.makeDrawingPane,
        drawingAreas = context.selectAll('g.chart-group')
            .data(areas),
        onlyOneAxis = this.onlyOneAxis,
        areasCount = areas.length;

    drawingAreas.enter()
        .append('svg:g')
        .attr('class', 'chart-group');
    drawingAreas.exit()
        .remove();

    drawingAreas.each(function(d, i){
        if (scales.x && scales.y) {
            var xScale = scales.x[i] || scales.x.first(),
                yScale = scales.y[i] || scales.y.first(),
                xAxis = d3.svg.axis()
                    .scale(xScale)
                    .tickSize(onlyOneAxis && i < areasCount - 1 ? 0 : 5)
                    .orient('bottom');

            // set the count of ticks in the x axis
            xAxis.ticks(onlyOneAxis && i < areasCount - 1 ? 0 : xTickCount);

            // render the x axis
            var xG = d3.select(this).selectAll('g.x.axis')
                    .data([1]);
            xG.enter()
                .append("svg:g");
            xG.exit()
                .remove();
            xG.attr("class", "x axis")
                .attr("transform", 'translate(' + d.x + ', ' + (d.y + d.height) + ')')
                .call(xAxis);

            // apply rotation for the x axis labels
            d3.select(this).selectAll('g.x.axis text')
                .attr('text-anchor',
                    (xLabelRotation === 0) ? 'middle' :
                    (xLabelRotation < 0) ? 'end' :
                    'start')
                .attr('transform', 'rotate('+xLabelRotation+')');
        } else {
            console.warn('Cannot draw axes when no scales are defined');
        }
        drawingPanes.push(makeDrawingPane(this, d));
    });
    return drawingPanes;
}

688.source:
function makeDrawingPane(context, area) {
    var clipId = 'clip-' + Math.floor(Math.random()*0x10000).toString(16),
        left = area.x,
        top = area.y,
        w = area.width,
        h = area.height;

    // Make sure we have a clip path
    var clipPath = d3.select(context).selectAll('clipPath.chart-content')
            .data([1]);
    clipPath.enter()
            .append("svg:clipPath")
            .attr('class', 'chart-content')
            .append("svg:rect")
    clipPath.exit().remove();
    clipPath.attr("id", clipId);
    clipPath.select('rect')
            .attr("width", w)
            .attr("height", h);

    // Make sure we have a drawing pane connected to that clipPath
    var drawingPane = d3.select(context).selectAll('g.drawing-pane')
            .data([1]);
    drawingPane.enter()
            .append("svg:g")
            .attr('class', 'drawing-pane');
    drawingPane.exit().remove();
    drawingPane.attr("clip-path", "url(#"+clipId+")")
            .attr("transform", "translate(" + left + "," + top + ")");

    return drawingPane;

}

