3212.source:
function applyLayout() {
    var oldModuleSet = this.oldModuleSet,
        newModuleSet,
        chart = this.owner.owner;

    // first apply the layout ...
    $super();

    if (chart && chart.isAncestorOf(this)) {

        this.owner.adjustHeight();
        newModuleSet = this.submorphs.sortBy(function(chartModuleMorph) {
            return chartModuleMorph.getPosition().y;
        });

        if (Arrays.equal(newModuleSet, oldModuleSet)) return;
        this.oldModuleSet = newModuleSet;
        chart.redraw();
        //this.unmarkAllMorphs();
        //this.owner.resetHookMarks();
        this.markHooks();
    } else {
        console.warn('Cannot draw because cannot find mother chart morph :(');
    }

}

