3793.source:
function markHook(morph, hook) {
    alert('This function (markHook) is actually not supposed to be called anymore ...');
    var mark = this.hookIcons[hook].copy();

    this.positionHookMarkForMorph(morph, mark);
    if (hook === 'drawSeries') {
        mark.setText(++this.seriesCounter);
    }
    mark.module = morph;
    this.addMorph(mark);
    if (!this.hookMarks) {
        this.hookMarks = [];
    }
    this.hookMarks.push(mark);
}

