function resetHookMarks() {
    alert('This function (resetHookMarks) is actually not supposed to be called anymore ...');
    if (this.hookMarks && this.hookMarks.each) {
        this.hookMarks.each(function(hookMark) {
            hookMark.remove();
        }, this);
    }
    this.seriesCounter = 0;
    this.hookMarks = []
}

