function setupDrawingAreas(data, area) {
    var w = area.width,
        h = area.height,
        p = this.padding,
        s = data.getSeries().length,
        r = this.rows || 1,
        panelsPerRow = Math.ceil(s/ r),
        panelsPerColumn = Math.ceil(s/ panelsPerRow),
        panelWidth = (w - (panelsPerRow - 1) * p) / panelsPerRow,
        panelHeight = (h- (panelsPerColumn - 1) * p) / panelsPerColumn,
        areas = [];

    for (var i = 0; i < s; i++) {
        var row = Math.floor(i/panelsPerRow),
            col = i % panelsPerRow;
        areas.push(new Rectangle(
            area.x + col * (p + panelWidth),
            area.y + row * (p + panelHeight),
            panelWidth,
            panelHeight
            ));
    }
    return areas;

}
