955.source:
function draw() {
    var chart = this.get('Chart');
    if (chart && chart.isAncestorOf(this)) {
        this.resetHookMarks();
        chart.draw();
    } else {
        console.warn('Cannot draw because cannot find mother chart morph :(');
    }
}

958.source:
function markHook(morph, hook) {
    alert('This function (markHook) is actually not supposed to be called anymore ...');
    var mark = this.hookIcons[hook].copy();

    this.positionHookMarkForMorph(morph, mark);
    if (hook === 'drawSeries') {
        mark.setText(++this.seriesCounter);
    }
    mark.module = morph;
    this.addMorph(mark);
    if (!this.hookMarks) {
        this.hookMarks = [];
    }
    this.hookMarks.push(mark);
}

961.source:
function resetHookMarks() {
    alert('This function (resetHookMarks) is actually not supposed to be called anymore ...');
    if (this.hookMarks && this.hookMarks.each) {
        this.hookMarks.each(function(hookMark) {
            hookMark.remove();
        }, this);
    }
    this.seriesCounter = 0;
    this.hookMarks = []
}

964.source:
function updateHookMarks() {
    alert('This function (updateHookMarks) is actually not supposed to be called anymore ...');
    this.seriesCounter = 0;
    if (this.hookMarks && this.hookMarks.each) {
        this.hookMarks.each(function(mark) {
            if (mark.module) this.positionHookMarkForMorph(mark.module, mark);
        }, this);
    }
}

967.source:
function positionHookMarkForMorph(morph, mark) {
    alert('This function (positionHookMarkForMorph) is actually not supposed to be called anymore ...');
    if (morph && mark && morph.owner) {
        var y = morph.getPosition().y + morph.owner.getPosition().y;
        mark.setPosition(pt(20, y + 8));
    }
}

