1656.source:
function onChartMouseWheel(evt) {
    var scaleFactor = (this.scaleFactor >= 0) ? this.scaleFactor : 1,
        scaleSlider = this.scaleSlider,
        wheelFactor = 0.001,
        wheelDelta = evt.wheelDeltaY;

    // Avoid page scrolling
    evt.preventDefault();

    if (scaleSlider) {
        var max = scaleSlider.valueScale,
            newScaleValue = scaleFactor + wheelDelta * wheelFactor;
        scaleSlider.value =
            (newScaleValue > max) ? max :
            (newScaleValue < 0) ? 0 :
            newScaleValue;
    }

}

