function setupScales(data, areas) {

    return areas.collect(function(area){
        var w = area.width,
            h = area.height,
            mode = this.mode || "orthographic",
            originLat = this.originLat,
            originLng = this.originLng,
            scaleModeFactor = (mode === 'equidistant') ? 1 / Math.PI * 2 :
                (mode === 'equalarea') ? 1 / Math.SQRT2 : 1,
            scaleFactor = (this.scaleFactor >= 0) ? this.scaleFactor : 1,
            scale = (w / h > 2 ? h*2 : w) * scaleFactor * scaleModeFactor,
            circleScale = scale / (this.circleScale || 1);

        return {
            x: d3.scale.linear().range([0, circleScale]).domain([0, 50]),
            loc: d3.geo.azimuthal()
                    .scale(scale)
                    .mode(mode)
                    .origin([originLng,originLat])
                    .translate([w/2, h/2])
        };
    }, this);
}

