2463.source:
function makeDrawingPane(context, area) {
    var clipId = 'clip-' + Math.floor(Math.random()*0x10000).toString(16),
        left = area.x,
        top = area.y,
        w = area.width,
        h = area.height;

    // Make sure we have a clip path
    var clipPath = d3.select(context).selectAll('clipPath.chart-content')
            .data([1]);
    clipPath.enter()
            .append("svg:clipPath")
            .attr('class', 'chart-content')
            .append("svg:rect")
    clipPath.exit().remove();
    clipPath.attr("id", clipId);
    clipPath.select('rect')
            .attr("width", w)
            .attr("height", h);

    // Make sure we have a drawing pane connected to that clipPath
    var drawingPane = d3.select(context).selectAll('g.drawing-pane')
            .data([1]);
    drawingPane.enter()
            .append("svg:g")
            .attr('class', 'drawing-pane');
    drawingPane.exit().remove();
    drawingPane.attr("clip-path", "url(#"+clipId+")")
            .attr("transform", "translate(" + left + "," + top + ")");

    return drawingPane;

}

2466.source:
function markHooks() {
    this.unmarkAllMorphs();

    var markFirst = ['prepareContext', 'setupDrawingAreas', 'drawDimensions'],
        markAll = ['setupScales','drawSeries'],
        markEvents = ['onChartMouseWheel', 'onChartDragStart','onChartDrag',
            'onChartDragEnd'],
        eventMorphs = [];
    markFirst.each(function(h){
        var morphs = this.getMorphsForHook(h);
        if (morphs.length > 0) {
            this.markMorph(morphs.first(), h);
        }
    }, this);
    markAll.each(function(h){
        this.getMorphsForHook(h).each(function(morph){
            this.markMorph(morph, h);
        }, this);
    }, this);

    markEvents.each(function(h){
        this.getMorphsForHook(h).each(function(morph){
            eventMorphs.pushIfNotIncluded(morph);
        }, this);
    }, this);
    eventMorphs.each(function(morph){
        this.markMorph(morph, 'Event');
    }, this);

    this.correctModuleLayouts();
}

