2472.source:
function markMorph(morph, hook) {
        //morph.addStyleClassName(this.activeRenderPartClassName);
        //morph.removeStyleClassName(this.inactiveRenderPartClassName)

        var icons = morph.submorphs
                .select(function(s) {return s.name.indexOf('Icon') >= 0}),
            hookLabeledIcons = icons.select(function(icon) {
                    return icon.hookName === hook;
                }),
            moduleTypes = this.moduleTypes,
            activeIconClass = this.activeRenderIconClassName;

        if (hookLabeledIcons.length < 1) {
            // In case module icons have no hookName attributes, try to
            // find the right icons over their names
            var hookNamePart = moduleTypes.select(function(s) {
                        return hook.indexOf(s) >= 0;
                    }).first();
            if (hookNamePart) {
                hookLabeledIcons = icons.select(function(icon) {
                        return icon.name.indexOf(hookNamePart) >= 0;
                    });
                if (hookLabeledIcons.length < 1) {
                    var renderIcon = this.getRenderIcon(hookNamePart);
                    if (renderIcon) {
                        morph.addMorph(renderIcon.copy());
                    }
                }
            }
        }

        // Add the class 'active' to each active hook icon
        hookLabeledIcons.each(function(icon) {
                icon.addStyleClassName(activeIconClass);
            });

        /*
        // The owner should be a ChartGenerator which 
        // implements a function to mark morph according to their hook
        if (this.owner.markHook) {
            this.owner.markHook(morph, hook);
        }
        */
        return morph;
}

2475.source:
function correctModuleLayouts() {
    // this.correctModuleLayouts();
    var defaultWidth = 285,
        defaultLabelPos = pt(10, 10),
        defaultIconPos = pt(224.0,11.0),
        defaultIconMargin = 25,
        defaultButtonPos = pt(251.0,9.0),
        moduleTypes = this.moduleTypes;

    this.submorphs.each(function(m) {
        // m = this.submorphs.first()
        m.setExtent(m.getExtent().withX(defaultWidth));

        var button = m.submorphs.detect(function(s) {
            return s.name && s.name.include('CollapseButton'); });
        button && button.setPosition(defaultButtonPos);

        var icons = m.submorphs
                .select(function(s) { return s.name && s.name.include('Icon'); })
                .sort(function(a,b) {
                        // Sort the icons by type
                        // (context should be left, series should be right ...)
                        var aType, bType; aType = bType = moduleTypes.length;
                        for (var i = 0; i< moduleTypes.length; i++) {
                            if (a.name && a.name.include(moduleTypes[i])) aType = i;
                            if (b.name && b.name.include(moduleTypes[i])) bType = i;
                        }
                        return aType - bType;
                    })
                .reverse();
        icons.each(function(icon, idx) {
            // Place each icon in its position in the icon row
            var iconShiftLeft = idx * defaultIconMargin,
                iconPosition = defaultIconPos.addXY(-iconShiftLeft, 0);
            icon.setPosition(iconPosition);
            icon.addStyleClassName('module-icon');
            icon.setAppearanceStylingMode(true);
        });

        var label = m.submorphs.detect(function(s) {return s.name && s.name.indexOf('Label'); });
        label && label.setPosition(defaultLabelPos);
    });
}

