function adjustForNewBounds() {
    $super();
    var marginLeft = 5,
        marginTop = 10,
        bounds = this.innerBounds(),
	y = bounds.y + marginTop / 2,
	width = bounds.width;
    this.submorphs.forEach(function(morph) {
	   var extent = morph.getExtent();
	   morph.setPosition(pt(marginLeft,y))
           y += extent.y + marginTop;
	})
}

