function doAction() {
    var baseUrl = 'http://earthquake.usgs.gov/earthquakes/catalogs/eqs7day-M1.txt',
        proxiedUrl = URL.makeProxied(new URL(baseUrl)),
        groupBy = function (source, iterator) {
            var groups = {};
            for (var i = 0, len = source.length; i < len; i++) {
                var hash = iterator(source[i], i);
                if (!groups[hash]) groups[hash] = [];
                groups[hash].push(source[i]);
            }
            return groups;
        },
        owner = this.owner;

    d3.csv('data-exploration/eqs7day-M1.txt',//proxiedUrl.fullPath(),
        function(data) {
            data.each(function(d) {
                    var date = d3.time.format('%A, %B%e, %Y %X UTC').parse(d.Datetime)
                        || d3.time.format('%A, %B %e, %Y %X UTC').parse(d.Datetime);
                    d.date = d3.time.format('%B %d, %Y')(date);
                });

            var quakeGroups = groupBy(data,
                function(ea) {
                    return ea['date'];
                }),
                i = 0,
                series = [];

            for (var x in quakeGroups) {
                series.push(quakeGroups[x]);
            }

            series.sortBy(function(s) {
                    return d3.time.format('%B %d, %Y').parse(s.first().date).getTime();
                })
                .reverse()
                .slice(0,6)
                .each(function(d, i) {
                    owner.updateSeries(i,
                        d.collect(function(d) {
                            return {
                                y: parseFloat(d.Magnitude),
                                x: d.Region,
                                loc:{
                                    lng:d.Lon,
                                    lat:d.Lat
                                    }
                                };
                        }),
                        d.first().date);
                });


        });


}

