        yDelta = evt.offsetY - this.startYDrag,
        lngStart = this.lngStart,
        latStart = this.latStart,
        scale = this.scaleFactor * 8,
        newLng =  (((lngStart - xDelta / scale)%360)+360)%360, // js modulo bug fix
        newLat =  (((latStart + yDelta / scale)%360)+360)%360;

    this.latSlider.value = newLat;
    this.lngSlider.value = newLng;
}

