        }
    }, this);
    markAll.each(function(h){
        this.getMorphsForHook(h).each(function(morph){
            this.markMorph(morph, h);
        }, this);
    }, this);

    markEvents.each(function(h){
        this.getMorphsForHook(h).each(function(morph){
            eventMorphs.pushIfNotIncluded(morph);
        }, this);
    }, this);
    eventMorphs.each(function(morph){
