function markMorph(morph, hook) {
        //morph.addStyleClassName(this.activeRenderPartClassName);
        //morph.removeStyleClassName(this.inactiveRenderPartClassName)

        var icons = morph.submorphs
                .select(function(s) {return s.name.indexOf('Icon') >= 0}),
            hookLabeledIcons = icons.select(function(icon) {
                    return icon.hookName === hook;
                }),
            moduleTypes = this.moduleTypes,
            activeIconClass = this.activeRenderIconClassName;

        if (hookLabeledIcons.length < 1) {
            // In case module icons have no hookName attributes, try to
            // find the right icons over their names
            var hookNamePart = moduleTypes.select(function(s) {
                        return hook.indexOf(s) >= 0;
                    }).first();
            if (hookNamePart) {
                hookLabeledIcons = icons.select(function(icon) {
                        return icon.name.indexOf(hookNamePart) >= 0;
                    });
                if (hookLabeledIcons.length < 1) {
                    var renderIcon = this.getRenderIcon(hookNamePart);
                    if (renderIcon) {
                        morph.addMorph(renderIcon.copy());
                    }
                }
            }
        }

        // Add the class 'active' to each active hook icon
        hookLabeledIcons.each(function(icon) {
                icon.addStyleClassName(activeIconClass);
            });

        /*
        // The owner should be a ChartGenerator which 
        // implements a function to mark morph according to their hook
        if (this.owner.markHook) {
            this.owner.markHook(morph, hook);
        }
        */
        return morph;
}

