241.source:
function drawSeries(contexts, data, scales) {
    var series = data.getSeries(),
        tension = this.tension || 1,
        interpolation = this.interpolation || 'basis';

    // Fix Array.toString(): [1,2,3,4] -> "1,2,3,4"
    Array.prototype.toString =
        function() {
            return this.reduce(
                function(previousValue, currentValue, index, array) {
                    return (previousValue.length > 0
                            ? previousValue +','
                            : '')
                            + currentValue;
                }, '');
        };
    if (scales.x && scales.y) {
        contexts.each(function(ctx, i){
            var s, ser,
                ctxSer = [],
                x = scales.x[i] || scales.x.first(),
                y = scales.y[i] || scales.y.first(),
                line = d3.svg.line()
                        .interpolate(interpolation)
                        .tension(tension)
                        .x(function(d) { return x(d.x); })
                        .y(function(d) { return y(d.y); });
            if (x && y) {
                for (var b = i; (s = series[b]); b += contexts.length) {
                    ctxSer.push(s);
                }
                ser = ctx.selectAll("path.series.line")
                    .data(ctxSer);
                ser.enter()
                    .insert("svg:path");
                ser.exit()
                    .remove();

                ser.each(function(d) {
                    d3.select(this)
                        .datum(d.values)
                        .attr("d", line)
                        .attr("class", 'series line'+ (d.className ? ' '+ d.className : ''));
                });
            } else {
                console.warn('Cannot draw lines when no scales are defined');
            }
        });
    }
}

