    var w = area.width,
        h = area.height,
        t = area.y,
        l = area.x,
        pt = this.paddingTop || 0,
        pb = this.paddingBottom || 0,
        pl = this.paddingLeft || 0,
        pr = this.paddingRight || 0;

    return [new Rectangle(l + pl, t + pt, w - pl - pr, h - pt - pb)];

}

