function setupScales(data, areas) {
    var xScales = areas.collect(function(area){
                var w = area.width,
                    h = area.height;
                return d3.scale.linear().range([0, w]).domain([this.xMin, this.xMax]);
            }, this),
        yScales = areas.collect(function(area){
                var w = area.width,
                    h = area.height;
                return d3.scale.linear().range([h, 0]).domain([this.yMin, this.yMax]);
            }, this);
    return {x: xScales, y: yScales};
}

