function onChartDrag(evt) {
    var xDelta = evt.clientX- this.startXDrag || 0,
        yDelta = evt.clientY - this.startYDrag || 0,
        xMinStart = this.xMinStart || 0,
        xMaxStart = this.xMaxStart || 0,
        yMaxStart = this.yMaxStart || 0,
        rangeFactor = (xMaxStart - xMinStart) / 300, //FIXME: has to adapt to larger chartareas
        yRangeFactor = yMaxStart / 300, //FIXME: has to adapt to larger chartareas
        newXMin = xMinStart - xDelta * rangeFactor,
        newXMax = xMaxStart - xDelta * rangeFactor,
        newYMax = yMaxStart + yDelta * yRangeFactor;
    if (this.xMinSlider && this.xMaxSlider && this.yMaxSlider) {
        this.xMinSlider.value = newXMin;
        this.xMaxSlider.value = newXMax;
        this.yMaxSlider.value = newYMax;
    }
}

1091.source:
function onChartDragStart(evt) {
    this.startXDrag = evt.clientX;
    this.startYDrag = evt.clientY;
    if (this.xMinSlider && this.xMaxSlider && this.yMaxSlider) {
        this.xMinStart = this.xMinSlider.value;
        this.xMaxStart = this.xMaxSlider.value;
        this.yMaxStart = this.yMaxSlider.value;
    }
}

1095.source:
function onChartMouseWheel(evt) {
    var xMinSlider = this.xMinSlider,
        xMaxSlider = this.xMaxSlider;

    if (xMinSlider && xMaxSlider) {
        var xMinStart = xMinSlider.value,
        xMaxStart = xMaxSlider.value,
        range = xMaxStart - xMinStart,
        wheelFactor = 0.001,
        wheelDelta = evt.wheelDeltaY,
        zoomAddRange = range * wheelDelta * wheelFactor;

        // Avoid page scrolling
        evt.preventDefault();

        xMinSlider.value = xMinStart + zoomAddRange;
        xMaxSlider.value = xMaxStart - zoomAddRange;
    } else {
        console.warn('Where are my (linear scales module) sliders? Cannot zoom!');
    }

}

