2191.source:
function setupScales(data, areas) {

    return {loc:  areas.collect(function(area){
            var w = area.width,
                h = area.height,
                mode = this.mode || "orthographic",
                originLat = this.originLat,
                originLng = this.originLng,
                scaleModeFactor = (mode === 'equidistant') ? 1 / Math.PI * 2 :
                    (mode === 'equalarea') ? 1 / Math.SQRT2 : 1,
                scaleFactor = (this.scaleFactor >= 0) ? this.scaleFactor : 1,
                scale = (w / h > 2 ? h*2 : w) * scaleFactor * scaleModeFactor,
                circleScale = scale / (this.circleScale || 1);

            return d3.geo.azimuthal()
                        .scale(scale)
                        .mode(mode)
                        .origin([originLng,originLat])
                        .translate([w/2, h/2]);
        }, this)
    }
}

2195.source:
function onChartDrag(evt) {
    var xDelta = evt.offsetX - this.startXDrag,
        yDelta = evt.offsetY - this.startYDrag,
        lngStart = this.lngStart,
        latStart = this.latStart,
        scale = this.scaleFactor * 8,
        newLng =  (((lngStart - xDelta / scale)%360)+360)%360, // js modulo bug fix
        newLat =  (((latStart + yDelta / scale)%360)+360)%360;

    this.latSlider.value = newLat;
    this.lngSlider.value = newLng;
}

2199.source:
function onChartDragStart(evt) {
    this.startXDrag = evt.offsetX;
    this.startYDrag = evt.offsetY;
    this.latStart = this.latSlider.value;
    this.lngStart = this.lngSlider.value;

}

2203.source:
function onChartMouseWheel(evt) {
    var scaleFactor = (this.scaleFactor >= 0) ? this.scaleFactor : 1,
        scaleSlider = this.scaleSlider,
        wheelFactor = 0.001,
        wheelDelta = evt.wheelDeltaY;

    // Avoid page scrolling
    evt.preventDefault();

    if (scaleSlider) {
        var max = scaleSlider.valueScale,
            newScaleValue = scaleFactor + wheelDelta * wheelFactor;
        scaleSlider.value =
            (newScaleValue > max) ? max :
            (newScaleValue < 0) ? 0 :
            newScaleValue;
    }

}

