3186.source:
function drawSeries(contexts, data, scales) {
    var series = data.getSeries(),
        offsetX = this.offsetX || 0,
        offsetY = this.offsetY || 0,
        lineHeight = this.lineHeight || 0;
    contexts.each(function(ctx, i){
        var s, ser,
            ctxSer = [];
        for (var b = i; (s = series[b]); b += contexts.length) {
            ctxSer.push(s);
        }
        ser = ctx.selectAll('text').data(ctxSer);
        ser.enter().insert("svg:text");
        ser.exit().remove();
        ser.text(function(d) {return d.title})
            .attr('x', offsetX)
            .attr('y', function(d, i) {return offsetY + i * lineHeight})
            .attr('class', function(d) {return'label'+ (d.className ? ' '+ d.className : '')});
    })

}

