function drawDimensionsDefault(context, dimensions, scales, areas) {
    var drawingPanes = [],
        xTickCount = this.xTickCount,
        makeDrawingPane = this.makeDrawingPane,
        drawingAreas = context.selectAll('g.chart-group')
            .data(areas);

    drawingAreas.enter()
        .append('svg:g')
        .attr('class', 'chart-group');
    drawingAreas.exit()
        .remove();

    drawingAreas.each(function(d, i){
        drawingPanes.push(makeDrawingPane(this, d));
    });
    return drawingPanes;
}

