3256.source:
function makeDrawingPane(context, area) {
    var clipId = 'clip-' + Math.floor(Math.random()*0x10000).toString(16),
        left = area.x,
        top = area.y,
        w = area.width,
        h = area.height;

    // Make sure we have a clip path
    var clipPath = d3.select(context).selectAll('clipPath.chart-content')
            .data([1]);
    clipPath.enter()
            .append("svg:clipPath")
            .attr('class', 'chart-content')
            .append("svg:rect")
    clipPath.exit().remove();
    clipPath.attr("id", clipId);
    clipPath.select('rect')
            .attr("width", w)
            .attr("height", h);

    // Make sure we have a drawing pane connected to that clipPath
    var drawingPane = d3.select(context).selectAll('g.drawing-pane')
            .data([1]);
    drawingPane.enter()
            .append("svg:g")
            .attr('class', 'drawing-pane');
    drawingPane.exit().remove();
    drawingPane.attr("clip-path", "url(#"+clipId+")")
            .attr("transform", "translate(" + left + "," + top + ")");

    return drawingPane;

}

3260.source:
function markHooks() {
    this.unmarkAllMorphs();

    var markFirst = ['prepareContext', 'setupDrawingAreas', 'drawDimensions'],
        markAll = ['setupScales','drawSeries'],
        markEvents = ['onChartMouseWheel', 'onChartDragStart','onChartDrag',
            'onChartDragEnd'],
        eventMorphs = [];
    markFirst.each(function(h){
        var morphs = this.getMorphsForHook(h);
        if (morphs.length > 0) {
            this.markMorph(morphs.first(), h);
        }
    }, this);
    markAll.each(function(h){
        this.getMorphsForHook(h).each(function(morph){
            this.markMorph(morph, h);
        }, this);
    }, this);

    markEvents.each(function(h){
        this.getMorphsForHook(h).each(function(morph){
            eventMorphs.pushIfNotIncluded(morph);
        }, this);
    }, this);
    eventMorphs.each(function(morph){
        this.markMorph(morph, 'Event');
    }, this);

    this.correctModuleLayouts();
}

3264.source:
function unmarkAllMorphs() {
    var activeIconClass = this.activeRenderIconClassName;
    this.submorphs.each(function(m){
        // Purge the active class from all module icons
        m.submorphs
            .select(function(s) {return s.name.indexOf('Icon') >= 0})
            .each(function(icon) {
                    icon.removeStyleClassName(activeIconClass);
                });

        //m.removeStyleClassName(this.activeRenderPartClassName);
        //m.addStyleClassName(this.inactiveRenderPartClassName);
    }, this);
}

3268.source:
function markMorph(morph, hook) {
        //morph.addStyleClassName(this.activeRenderPartClassName);
        //morph.removeStyleClassName(this.inactiveRenderPartClassName)

        var icons = morph.submorphs
                .select(function(s) {return s.name.indexOf('Icon') >= 0}),
            hookLabeledIcons = icons.select(function(icon) {
                    return icon.hookName === hook;
                }),
            moduleTypes = this.moduleTypes,
            activeIconClass = this.activeRenderIconClassName;

        if (hookLabeledIcons.length < 1) {
            // In case module icons have no hookName attributes, try to
            // find the right icons over their names
            var hookNamePart = moduleTypes.select(function(s) {
                        return hook.indexOf(s) >= 0;
                    }).first();
            if (hookNamePart) {
                hookLabeledIcons = icons.select(function(icon) {
                        return icon.name.indexOf(hookNamePart) >= 0;
                    });
                if (hookLabeledIcons.length < 1) {
                    var renderIcon = this.getRenderIcon(hookNamePart);
                    if (renderIcon) {
                        morph.addMorph(renderIcon.copy());
                    }
                }
            }
        }

        // Add the class 'active' to each active hook icon
        hookLabeledIcons.each(function(icon) {
                icon.addStyleClassName(activeIconClass);
            });

        /*
        // The owner should be a ChartGenerator which 
        // implements a function to mark morph according to their hook
        if (this.owner.markHook) {
            this.owner.markHook(morph, hook);
        }
        */
        return morph;
}

3272.source:
function correctModuleLayouts() {
    var defaultWidth = 285,
        defaultLabelPos = pt(10, 10),
        defaultIconPos = pt(224.0,11.0),
        defaultIconMargin = 25,
        defaultButtonPos = pt(251.0,9.0),
        moduleTypes = this.moduleTypes;

    this.submorphs.each(function(m) {
        m.setExtent(m.getExtent().withX(defaultWidth));

        var button = m.submorphs
                .select(function(s) {return s.name.indexOf('CollapseButton') >= 0})
                .first();
        if (button) {
            button.setPosition(defaultButtonPos);
        }

        var icons = m.submorphs
                .select(function(s) {return s.name.indexOf('Icon') >= 0})
                .sort(function(a,b) {
                        // Sort the icons by type
                        // (context should be left, series should be right ...)
                        var aType = bType = moduleTypes.length;
                        for (var i = 0; i< moduleTypes.length; i++) {
                            if (a.name.indexOf(moduleTypes[i]) >= 0) {
                                aType = i;
                            }
                            if (b.name.indexOf(moduleTypes[i]) >= 0) {
                                bType = i;
                            }
                        }
                        return aType - bType ;
                    })
                .reverse();
        icons.each(function(icon, idx) {
            // Place each icon in its position in the icon row
            var iconShiftLeft = idx * defaultIconMargin,
                iconPosition = defaultIconPos.addXY(-iconShiftLeft, 0);
            icon.setPosition(iconPosition);
            icon.addStyleClassName('module-icon');
            icon.setAppearanceStylingMode(true);
        });

        var label = m.submorphs
                .select(function(s) {return s.name.indexOf('Label') >= 0})
                .first();
        if (label) {
            label.setPosition(defaultLabelPos);
        }
    })
}

