function addMorph(morph) {
    //debugger
    if (this.owner.chartGenerator) {
        var renderer = this.owner.chartGenerator.get('ChartRenderer');
        if (renderer && this.owner.chartGenerator.isAncestorOf(renderer)) {
            renderer.addMorph(morph);
            this.owner.draw();
        }
    }
}

