3757.source:
function toggleGeneratorPane() {
    var pane = this.get('ChartGenerator'),
        steps = 5, timePerStep = 10,
        btn = this.get('GeneratorPaneToggleButton');

    // Avoid spare modules occluding the chart
    this.bringToFront();

    if (pane) {
        var moveOffset = pane.getExtent().withY(0),
            dest = pane.getPosition().addPt(moveOffset.negated());
        pane.animatedInterpolateTo(dest, steps, timePerStep, function() {
            btn.setLabel('>');
            pane.remove();
        });
        this.chartGenerator = pane;
    } else if (this.chartGenerator) {
        var moveOffset = this.chartGenerator.getExtent().withY(0);
        btn.setLabel('<')
        this.addMorphBack(this.chartGenerator);
        this.chartGenerator.align(
            this.chartGenerator.bounds().topRight(),
            btn.bounds().topRight());
        // move it so that it is completely visible
        var dest = this.chartGenerator.getPosition().addPt(moveOffset);
        this.chartGenerator.animatedInterpolateTo(dest, steps, timePerStep, Functions.Null);
        this.chartGenerator.get('ChartRenderer').applyLayout();
    } else {
        alert('Something is messed up here: could not find the chart generator pane :(');
    }
}

3761.source:
function draw(clearBeforeDrawing) {
    var chartDisplay = this.get('ChartDisplay'),
        chartRenderer = this.chartGenerator.get('ChartRenderer'),
        chartData = this.chartData || new apps.Charting.ChartData();

    if (!chartRenderer) {
        alert('Charting morph is broken. Cannot find render morph. Where is it?');
        return
    }
    if (!chartDisplay || !this.isAncestorOf(chartDisplay)) {
        alert(
            'Charting morph is broken. Cannot find chart display morph. Please find it for me.');
        return
    }
    if (clearBeforeDrawing) {
        chartDisplay.clear();
    }
    if (chartData.getSeries) {
        chartDisplay.draw(chartData, chartRenderer);
    } else {
        console.warn('Chart has no data to draw (or data object is broken).')
    }
}

