function getDatabases() {
    var url = new URL(
        "http://www.lively-kernel.org/couchdb/metadata/_design/blogdbs/_view/getblogdbs"
    );
    this.setList(
        JSON.unserialize(new WebResource(url).get().content).
            rows.collect(function(row) {return row.key})
    );
}

