function addEntry(doWithEntry) {
    var journal  = this,
        entry = this.world().openPartItem("BlogEntry", "PartsBin/BP2012");

    entry.setLayouter(new lively.morphic.Layout.JournalLayout())
    entry.setPosition(pt(-1,0));
    entry.isJournalEntry = true;
    entry.applyStyle({resizeWidth: true})

    doWithEntry && doWithEntry(entry)

    journal.addMorph(entry)   
    this.get('buttonsPane').setPosition(pt(0,0))
    journal.adjustForNewBounds();
}

