function setMailContent(options) {
    if (options.content) {
        this.get('ContentText').textString = options.content;
    }
    if (options.subject) {
        this.get('SubjectText').textString = options.subject;
    }
    if (options.addressees) {
        var toString  = Object.isArray(options.addressees) ?
            options.addressees.join(',') : options.addressees;
        this.get('ToText').textString = toString;
    }
}

