function sendMail(options) {
    var content = this.get('ContentText').textString || '',
        subject = this.get('SubjectText').textString || '',
        to = this.get('ToText').textString || '',
        mailtoString = Strings.format("mailto:?to=%s&subject=%s&body=%s",
            to, Global.escape(subject), Global.escape(content));
    window.open(mailtoString);
    Global.LastMailMorph = this;
    this.remove();
}

