function displaySourceForScript(scriptName) {
    if (!scriptName) return;
    var code = "", that = this;
    if (scriptName === "-- ALL --") {
         this.sortedScriptNamesOfObj(this.target).forEach(function(each) {
            code = code.concat(that.generateSourceForScript(each)).concat("\n\n");
        });
    } else {
        code = this.generateSourceForScript(scriptName); 
    }
    this.displayJavaScriptSource(code);
}

795.source:
