1122.source:
function createGroupTree() {
    // groups
    var morphList = this.world().indentedListItemsOfMorphNames(),
        that = this,
        children = morphList.collect(function (element) {
                    return element.value.behaviorGroups
                }).compact().flatten().uniq().sort().collect(function (groupName) {
                    var selection = that.get("MagnifierButton").newMultiSelection()
                    selection.targets = morphList.select(function (element) {
                        return element.value.behaviorGroups &&
                                element.value.behaviorGroups.indexOf(groupName) >= 0
                    }).collect(function (element) {
                        return element.value
                    })
                    selection.setName(groupName)
                    var listName = groupName + " [" + selection.targets.length + "]"
                    return {
                        isListItem: true,
                        selectionString: listName,
                        name: listName,
                        editorPane: that.owner,
                        value: selection
                    }
                })

    children.each(function (c) {
        Object.addScript(c, function onSelect() {
            this.editorPane.setTarget(this.value)
        })
    })

    if (children.length === 0) {
        children = null
    }

    return {
        isListItem: true,
        selectionString: 'Groups',
        name: 'Groups',
        value: 'groups',
        children: children
    };
}

1125.source:
function createWorldTree() {
    var scripts = [
            function onSelect(tree) {
                if (window.event.isShiftDown()) {
                    this.editorPane.get("MorphSelectorTree").stayUp = true
                    if (!this.editorPane.target) {
                        var targets = []
                    } else if (this.editorPane.target.isMorph) {
                        var targets = [this.editorPane.target]
                    } else {
                        var targets = this.editorPane.target.targets
                    }
                    var idx = targets.indexOf(this.value)
                    if (idx >= 0) {
                        this.selector.removeHighlight(tree)
                        targets.removeAt(idx)
                    } else {
                        this.selector.setHighlight(tree)
                        targets.push(this.value)
                    }
                    if (targets.length === 0) {
                        this.editorPane.reset()
                    } else {
                        this.editorPane.setTarget(targets)
                    }
                } else {
                    this.editorPane.setTarget(this.value)
                }
            }
        ],
        properties = {
            editorPane: this.owner,
            selector: this
        }

    return {
        name: 'World',
        value: this.world(),
        editorPane: this.owner,
        selector: this,
        onSelect: scripts[0],
        children: this.world().submorphs.invoke('treeItemsOfMorphNames',
                                                {scripts: scripts,
                                                 properties: properties,
                                                 showUnnamed: true}).compact()
    };
}

1128.source:
function highlightCurrentTarget(tree) {
    var target = this.owner.target,
        nodes = tree.childNodes,
        highlightNode

    if (!target) {
        return
    } else if (target.isMorph) {
        var expandables = [target],
            nextOwner = target,
            currentNode
        while (nextOwner.owner) {
            expandables.push(nextOwner.owner)
            nextOwner = nextOwner.owner
        }
        expandables.reverse().each(function (m) {
            currentNode = nodes.detect(function (n) {
                return n.item.value === m
            })
            if (currentNode) {
                nodes = currentNode.childNodes
                if (!nodes && currentNode.item.children) {
                    currentNode.expand()
                    nodes = currentNode.childNodes
                }
            } else {
                return
            }
        })
        if (currentNode && currentNode.item.value === target) {
            highlightNode = currentNode
        }
    } else {
        var groupNodes = nodes.detect(function (n) {
            return n.item.value === 'groups'
        }).childNodes
        highlightNode = groupNodes.detect(function (n) {
            // group names are unique
            return n.item.value.name === target.name
        })
    }

    if (highlightNode) {
        this.setHighlight(highlightNode)
    }
}

