function onFire() {
    if (!(this.owner.target && this.owner.target.targets)) {
        return
    }
    var that = this
    var oldName = this.owner.target.name
    this.world().prompt("Please enter a name for this group", function (newName) {
        if (newName !== oldName) {
            that.owner.target.targets.each(function (target) {
                if (!target.behaviorGroups) {
                    target.behaviorGroups = []
                } else {
                    var idx = target.behaviorGroups.indexOf(oldName)
                    if (idx > -1) {
                        target.behaviorGroups.removeAt(idx)
                    }
                }
                if (newName !== "") {
                    target.behaviorGroups.push(newName)
                }
            })
            if (newName !== "") {
                that.owner.target.setName(newName)
            } else {
                that.owner.target.setName(undefined)
            }
        }
        that.get("ObjectEditorMorphSelector").setTarget(that.owner.target)
    }, oldName)
}

