function updateListItems() {
    this.clearList();
    if (!this.targetMorph) {
        return;
    }

    var lm = projects.WebDev2012.LimeMachine.LimeMachine;
    var history = lm.getWorldHistory();

    if (!history) {
        return;
    }
    var currentObject = history.getObject(this.targetMorph.id);
    if (!currentObject) {
        return;
    }

    this.currentCodeScripts = currentObject.Code;

    var listItems = [];
    var methodFilter = this.SelectedScript;
    var filteredScripts = this.currentCodeScripts.select(function(item) { return methodFilter == null || item.MethodName == methodFilter});
    var that = this;
    $.each(filteredScripts, function(idx, item) {
        item.toString = function() { return this.MethodName + " - " + that.getFormattedTime(this.Timestamp) };
        listItems.push(item);
    });

    this.updateList(listItems.reverse());
}

