1652.source:
function getFormattedTime(timestamp) {
    var today = new Date();
    today.setHours(0);
    today.setMinutes(0);
    today.setSeconds(0);
    today.setMilliseconds(0);

    var timestampTime = new Date(timestamp);

    if (timestampTime >= today)
        return timestampTime.format("HH:MM");
    else 
        return timestampTime.format("dd.mm.yyyy HH:MM");
}

