function generateSourceForScript(scriptName) {
    var script = this.target[scriptName],
        annotation = '',
        scriptSource = script.sourceString ||
                       script.getOriginal().sourceString ||
                       String(script);

    if (!script && !this.target.isMorph) {
        script = this.target.getFunction(scriptName)
    }

    scriptSource = this.removeTimeStampComment(scriptSource);

    if (script.timestamp && script.user) 
        annotation = Strings.format('// changed on %s by %s  \n', script.timestamp, script.user);

    // FIXME: fix getOriginal!
    scriptSource = Strings.format('%s', scriptSource);

    return annotation + scriptSource;
}

