4337.source:
function generateSourceForConnection(connection) {
    var c = connection, targetObject = this.target;
	if (!c.getTargetObj() || !c.getTargetObj().name || 
		!c.getSourceObj() || !c.getSourceObj().name) return String(c);

	var optConfig = []
	if (c.converterString)
		optConfig.push("converter: \n\t" + c.converterString)
	if (c.updaterString)
		optConfig.push("updater: \n\t" + c.updaterString)
	return Strings.format('connect(%s, "%s", %s, "%s", {%s});', 
			this.generateTargetCode(targetObject, c.getSourceObj()),
			c.getSourceAttrName(),
			this.generateTargetCode(targetObject, c.getTargetObj()),
			c.getTargetMethodName(),
			optConfig.join(','));

}

4341.source:
function generateSourceForScript(scriptName) {
    var script = this.target[scriptName],
        annotation = '',
        scriptSource = script.sourceString ||
                       script.getOriginal().sourceString ||
                       String(script);

    if (!script && !this.target.isMorph) {
        script = this.target.getFunction(scriptName)
    }

    scriptSource = this.removeTimeStampComment(scriptSource);

    if (script.timestamp && script.user) 
        annotation = Strings.format('// changed on %s by %s  \n', script.timestamp, script.user);

    // FIXME: fix getOriginal!
    scriptSource = Strings.format('%s', scriptSource);

    return annotation + scriptSource;
}

4345.source:
function generateTargetCode(baseObject, targetObject) {
	var name = targetObject.name;
	if (baseObject === targetObject) 
		return "this";
	else if (baseObject[name] ===  targetObject) 
		return "this." + name;
	else if (baseObject.testObject ===  targetObject) 
		return "this.testObject";
	else if (baseObject.get(name) === targetObject) 
		return 'this.get("' + name + '")';
	else if (targetObject instanceof Morph) 
		return '$morph("' + name + '")';
	else 
		return "????";
}

