function setTarget(morph) {
    this.reset();
    this.target = morph;
    this.get("NameGroupButton").setVisible(!morph.isMorph)
    // For arrays that are all morphs, create a group
    if (morph instanceof Array && morph.length > 0 && morph.all(function (e) {
            return e.isMorph
        })) {
        var group = this.get("MagnifierButton").newMultiSelection()
        group.targets = morph
        this.target = morph = group
    }

    this.migrateOldScriptAnnotations();

    this.morphSelector.setTarget(morph);
    this.scriptPane.setTarget(morph);

    this.updateLists();
    this.displayInitialScript();

    this.historyList.setTarget(morph);

    this.startStepping(500/*ms*/, 'update');  
}

