function onFire() {
    var targetMorph = this.owner.historyList.targetMorph;
    if (!targetMorph)
        return;

    var recoveryCheck = confirm("Do you really want to restore all locally stored script versions?");
    if (!recoveryCheck)
        return;

    var lm = projects.WebDev2012.LimeMachine.LimeMachine;
    var history = lm.getWorldHistory();

    if (!history) {
        return;
    }

    var currentObject = history.getObject(targetMorph.id);
    if (!currentObject) {
        return;
    }

    var currentCodeScripts = currentObject.Code;
    var that = this;
    var selectedScriptName = this.owner.scriptList.getSelection();

    $.each(currentCodeScripts, function(idx, item) {
        var success = that.owner.scriptPane.boundEval(item.Code);
        if(!success) {
            alert("Error while recovering " + item.MethodName + ".");
        }
    });

    this.owner.scriptList.selectAddedScript(selectedScriptName);
}

6416.source:
function disconnectSelectedConnection() {

    var editor = this.objectEditorPane;
    var selection = editor.connectionList.selection;

    if (!editor.target || editor.connectionList.getList().size() < 2) 
        return;

    var disconnectConnection = function (confirmed) {
        if (!confirmed) return;

        var listIndex = editor.target.attributeConnections.indexOf(selection[1]);
        if (selection && (typeof selection !== "string") && listIndex > -1) {
            var c = selection[1];
            disconnect(c.sourceObj, c.sourceAttrName, c.targetObj, c.targetMethodName);
            editor.updateLists();
            editor.displayInitialScript();
        } 
    }

    var message = 'Disconnect "' + selection[0] +'" connection?';
    var callback = disconnectConnection;

    var dialog = new lively.morphic.ConfirmDialog(message, callback);

    return this.world().openDialog(dialog);
}

