6562.source:
function sortedScriptNamesOfObj(obj) {

    if (!obj)
        return [];

    if (obj && obj.isMorph) {

        if (!Functions.own(obj) ||  Functions.own(obj).size() == 0) return [];

        var selectedScripts = Functions.own(obj).
    	select(function(name) { return obj[name].getOriginal().hasLivelyClosure }).
    	sortBy(function(name) { return name.toLowerCase() });

        if (!this.currentTag) return selectedScripts;

        var that = this;
        var selectedScripts = selectedScripts.select(function(scriptName) {
            return obj[scriptName].tags && 
                obj[scriptName].tags.include(that.currentTag);
        });

        return selectedScripts;
    } else  {
        // obj is a multiselection-target group 
        return obj.targetFunctions();
    }


}

6565.source:
function updateLists() {

    var scriptListItems = this.sortedScriptNamesOfObj(this.target);
    scriptListItems.unshift("-- ALL --");
    if (!Arrays.equal(scriptListItems, this.scriptList.getList())) {
        this.scriptList.setList(scriptListItems);
    }

    var connectionListItems = this.sortedConnectionNamesOfObj(this.target);
    connectionListItems.unshift("-- ALL --");
    if (!Arrays.equal(connectionListItems, this.connectionList.getList())) {
        this.connectionList.setList(connectionListItems);
    }
}

6569.source:
function onShutdown() {
    if (this.scriptPane.hasChanged()) {
        var that = this;
        // callback is executed asynchronously since waits on user input.
        // however, the editor should not be closed before input
        // therefore, we patch the remove method until the user reacted
        this.owner.remove = function() {};
        var callback = function(confirmed) {
            if (that.owner.state === "shutdown") delete that.owner.state;
            that.owner.remove = that.owner.__proto__.remove;
            if (confirmed) that.owner.remove();
        }
        this.confirmUnsavedChanges(callback);
    } 
}

